
public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int ans = 3;

		switch (ans) {
		case 0:
			System.out.println("zero");
			break;
		case 1:
			System.out.println("one");
			break;
		case 2:
			System.out.println("two");
			break;
		default:
			System.out.println("Error");
		}
	}

}
